import React from 'react';
import { cn } from '@/lib/utils';

export interface AnimatedButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  /**
   * The variant of the button
   * @default 'default'
   */
  variant?: 'default' | 'primary' | 'secondary' | 'ghost' | 'outline';
  /**
   * Whether the button has a subtle animation
   * @default true
   */
  animated?: boolean;
  /**
   * The size of the button
   * @default 'default'
   */
  size?: 'sm' | 'default' | 'lg';
}

const AnimatedButton = React.forwardRef<HTMLButtonElement, AnimatedButtonProps>(
  ({ variant = 'default', animated = true, size = 'default', className, children, ...props }, ref) => {
    return (
      <button
        ref={ref}
        className={cn(
          'inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50',
          // Size variants
          size === 'sm' && 'h-9 rounded-md px-3',
          size === 'default' && 'h-10 px-4 py-2',
          size === 'lg' && 'h-11 rounded-md px-8',
          // Base styles
          'relative overflow-hidden',
          // Variant styles with dark mode support
          variant === 'default' && 
            'bg-primary text-primary-foreground shadow hover:bg-primary/90 dark:bg-primary/90 dark:hover:bg-primary/80',
          variant === 'primary' && 
            'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg hover:shadow-blue-500/30 dark:from-blue-600 dark:to-purple-700',
          variant === 'secondary' && 
            'bg-secondary text-secondary-foreground shadow-sm hover:bg-secondary/80 dark:bg-secondary/80 dark:hover:bg-secondary/70',
          variant === 'ghost' && 
            'hover:bg-accent hover:text-accent-foreground dark:hover:bg-accent/80 dark:hover:text-accent-foreground',
          variant === 'outline' && 
            'border border-input bg-background shadow-sm hover:bg-accent hover:text-accent-foreground dark:border-input/50 dark:bg-background/50 dark:hover:bg-accent/30',
          // Enhanced animation classes
          animated && 'transform transition-all duration-300 ease-in-out hover:-translate-y-0.5 hover:shadow-xl active:scale-95',
          className
        )}
        aria-label={props['aria-label'] || undefined}
        role="button"
        tabIndex={props.disabled ? -1 : 0}
        {...props}
      >
        {animated && (
          <>
            <span 
              className="absolute inset-0 h-full w-full bg-gradient-to-r from-transparent via-white/20 to-transparent opacity-0 transition-opacity duration-500 group-hover:opacity-100 -skew-x-12 -translate-x-full group-hover:translate-x-full" 
              aria-hidden="true"
            />
            <span 
              className="absolute h-full w-full bg-white opacity-0 transition-opacity duration-300 group-hover:opacity-5 dark:bg-black/10" 
              aria-hidden="true"
            />
          </>
        )}
        <span className="relative z-10">{children}</span>
      </button>
    );
  }
);

AnimatedButton.displayName = 'AnimatedButton';

export { AnimatedButton };
